mapper.select:
select id,declaration_type,project_type,project_type_detail,company_name,project_name,status,approval_status,DATE_FORMAT(create_time,'%Y-%m-%d') create_time from industrial_declaration
where del_flag = 0

    and `status` = #{status}


    and `declaration_type` = #{declarationType}


    and `project_type` = #{projectType}


    and `declaration_industry` = #{declarationIndustry}


    and `company_name` = #{companyName}


    and year(`create_time`) between #{declarationYearBegin} and #{declarationYearEnd}


    and year(`create_time`) >= #{declarationYearBegin}


    and year(`create_time`) <= #{declarationYearEnd}


    and `approval_status` = #{approvalStatus}


    and (`declaration_type` like CONCAT('%',#{keyWord},'%') or `project_name` like CONCAT('%',#{keyWord},'%') or `project_type` like CONCAT('%',#{keyWord},'%'))

order by create_time desc
