mapper.select:
select  from professionals
where del_flag = 0

    and user_name=#{userName}


    and engaged_industry=#{engagedIndustry}


    and engaged_industry_detail=#{engagedIndustryDetail}


    and industry_experience=#{industryExperience}


    and technology_scope in ('${technologyScope}')


    and technology_scope_datail = #{technologyScopeDetail}


    and (research_direction like CONCAT('%',#{keyWord},'%') or real_name like CONCAT('%',#{keyWord},'%') or institutions like CONCAT('%',#{keyWord},'%') or technology_scope like CONCAT('%',#{keyWord},'%'))


    and status=#{status}


    and approval_status=#{approvalStatus}
